import { useState, useRef } from 'react';
import axios from 'axios';
import { sampleHospitals, sampleEmergencyHospitals } from './sampleData';
import { assessmentService } from './symptomAssessment';
import { 
  routingConfig, 
  progressiveSearch, 
  enrichAndSortFacilities, 
  getSeverityBadge,
  getExpansionMessage 
} from './facilityRouting';

// Pincode to coordinates mapping (sample data)
const pincodeCoordinates = {
  '560001': { lat: 12.9716, lng: 77.5946, area: 'MG Road, Bangalore' },
  '560002': { lat: 12.9822, lng: 77.5985, area: 'Shivaji Nagar, Bangalore' },
  '560003': { lat: 12.9634, lng: 77.6089, area: 'Ulsoor, Bangalore' },
  '560038': { lat: 12.9784, lng: 77.6408, area: 'Indiranagar, Bangalore' },
  '560095': { lat: 12.9352, lng: 77.6245, area: 'Koramangala, Bangalore' },
  '560076': { lat: 12.9100, lng: 77.5950, area: 'Bannerghatta Road, Bangalore' },
  '560066': { lat: 12.9698, lng: 77.7499, area: 'Whitefield, Bangalore' },
  '560041': { lat: 12.9250, lng: 77.5838, area: 'Jayanagar, Bangalore' }
};

function AppWithSymptoms() {
  // Phase management: 1 = landing page with pincode + symptoms, 2 = results
  const [currentPhase, setCurrentPhase] = useState(1);
  const [pincode, setPincode] = useState('');
  const [selectedSymptoms, setSelectedSymptoms] = useState([]);
  const [condition, setCondition] = useState('');
  const [isEmergency, setIsEmergency] = useState(false);
  const [assessmentResult, setAssessmentResult] = useState(null);
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [map, setMap] = useState(null);
  const [searchRadius, setSearchRadius] = useState(null);
  const markersRef = useRef([]);
  const [useSampleData, setUseSampleData] = useState(true);
  const [expansionMessage, setExpansionMessage] = useState(null);

  // Handle pincode search with severity-based routing
  const handleSearch = async (e) => {
    if (e) e.preventDefault();

    if (!pincode || pincode.length !== 6) {
      alert('Please enter a valid 6-digit pincode');
      return;
    }

    // Check if we have symptoms or condition text
    if (selectedSymptoms.length === 0 && !condition.trim()) {
      alert('Please select symptoms or describe your condition');
      return;
    }

    setLoading(true);
    setRecommendations([]);
    setExpansionMessage(null);

    try {
      // Step 1: Assess symptoms to get severity
      const assessment = await assessmentService.assess(selectedSymptoms, condition);
      setAssessmentResult(assessment);

      // Step 2: Get pincode coordinates (centroid of the area)
      const pincodeData = pincodeCoordinates[pincode];
      if (!pincodeData && !useSampleData) {
        alert('Pincode not found. Please try a different pincode.');
        setLoading(false);
        return;
      }

      // Use default coordinates if pincode not in our sample data
      const centerLat = pincodeData?.lat || 12.9716;
      const centerLng = pincodeData?.lng || 77.5946;

      // Use sample data if toggle is enabled
      if (useSampleData) {
        setTimeout(() => {
          // Get all hospitals based on severity
          const allHospitals = assessment.severityLevel === 'emergency' 
            ? sampleEmergencyHospitals 
            : sampleHospitals;

          // Enrich with distances from pincode centroid
          const enriched = enrichAndSortFacilities(allHospitals, centerLat, centerLng);

          // Apply severity-based routing with progressive search
          const searchResult = progressiveSearch(enriched, assessment.severityLevel);

          setRecommendations(searchResult.facilities);
          setSearchRadius(searchResult.radiusUsed);

          // Show expansion message if radius was expanded
          if (searchResult.wasExpanded) {
            const config = routingConfig[assessment.severityLevel];
            setExpansionMessage(getExpansionMessage(config.initialRadius, searchResult.radiusUsed));
          }

          // Check if no facilities found
          if (searchResult.facilities.length === 0) {
            setExpansionMessage(getExpansionMessage(20, null));
          }

          setCurrentPhase(2);
          setLoading(false);

          // Initialize map after phase transition
          setTimeout(() => {
            initializeMap(searchResult.facilities, searchResult.radiusUsed, centerLat, centerLng);
          }, 100);
        }, 800);
        return;
      }

      // API call logic (for future backend integration)
      const endpoint = '/api/hospitals/severity-based';
      const response = await axios.post(endpoint, {
        pincode,
        latitude: centerLat,
        longitude: centerLng,
        severity: assessment.severity,
        severityLevel: assessment.severityLevel,
        specialties: assessment.specialties
      });

      const searchResult = response.data;
      setRecommendations(searchResult.facilities);
      setSearchRadius(searchResult.radiusUsed);

      if (searchResult.wasExpanded) {
        setExpansionMessage(searchResult.expansionMessage);
      }

      if (searchResult.facilities.length > 0) {
        setCurrentPhase(2);
        setTimeout(() => {
          initializeMap(searchResult.facilities, searchResult.radiusUsed, centerLat, centerLng);
        }, 100);
      } else {
        setExpansionMessage(getExpansionMessage(20, null));
      }
    } catch (error) {
      console.error('Error searching hospitals:', error);
      alert('Unable to find hospitals. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const initializeMap = (hospitals, radius) => {
    if (!window.L) return;

    const mapContainer = document.getElementById('care-map');
    if (!mapContainer) return;

    // Clear existing map
    if (map) {
      map.remove();
      setMap(null);
    }

    // Calculate center from hospitals
    let centerLat = 12.9716;
    let centerLng = 77.5946;

    const hospitalsWithCoords = hospitals.filter(h => h.latitude && h.longitude);
    if (hospitalsWithCoords.length > 0) {
      centerLat = hospitalsWithCoords.reduce((sum, h) => sum + parseFloat(h.latitude), 0) / hospitalsWithCoords.length;
      centerLng = hospitalsWithCoords.reduce((sum, h) => sum + parseFloat(h.longitude), 0) / hospitalsWithCoords.length;
    }

    // Initialize map
    const newMap = window.L.map('care-map', {
      center: [centerLat, centerLng],
      zoom: radius <= 3 ? 14 : radius <= 5 ? 13 : radius <= 10 ? 12 : 11,
      zoomControl: false,
      attributionControl: true
    });

    // Add tiles
    window.L.tileLayer('https://{s}.basemaps.cartocdn.com/light_all/{z}/{x}/{y}{r}.png', {
      attribution: '© OpenStreetMap © CARTO',
      maxZoom: 19,
      className: 'map-tiles'
    }).addTo(newMap);

    // Draw radius circle
    const config = routingConfig[assessmentResult.severityLevel];
    window.L.circle([centerLat, centerLng], {
      radius: radius * 1000, // Convert km to meters
      color: config.color,
      fillColor: config.color,
      fillOpacity: 0.1,
      weight: 2,
      dashArray: '5, 10'
    }).addTo(newMap);

    // Add hospital markers
    const newMarkers = [];
    hospitals.forEach((hospital, index) => {
      const lat = parseFloat(hospital.latitude);
      const lng = parseFloat(hospital.longitude);

      const markerHtml = `
        <div class="relative flex flex-col items-center">
          <div class="size-10 rounded-full border-2 border-white shadow-lg flex items-center justify-center text-white transform hover:scale-110 transition-transform cursor-pointer" style="background-color: ${config.color}">
            <span class="material-symbols-outlined text-[20px]">local_hospital</span>
          </div>
          <div class="absolute -z-10 size-10 rounded-full animate-ping opacity-30" style="background-color: ${config.color}"></div>
        </div>
      `;

      const marker = window.L.marker([lat, lng], {
        icon: window.L.divIcon({
          html: markerHtml,
          className: 'custom-hospital-marker',
          iconSize: [40, 40],
          iconAnchor: [20, 40],
          popupAnchor: [0, -40]
        })
      }).addTo(newMap);

      const popupContent = `
        <div class="p-3 min-w-[200px]">
          <h3 class="font-bold text-slate-900 text-sm mb-2">${hospital.name}</h3>
          <div class="space-y-1 text-xs">
            <div class="flex items-center gap-2">
              <span class="inline-block w-2 h-2 rounded-full" style="background-color: ${config.color}"></span>
              <span class="font-semibold">${hospital.facility_type}</span>
            </div>
            <div class="flex items-center gap-2 text-slate-600">
              <span class="material-symbols-outlined text-[14px]">location_on</span>
              <span>${hospital.address}</span>
            </div>
            ${hospital.phone ? `
              <div class="flex items-center gap-2 text-slate-600">
                <span class="material-symbols-outlined text-[14px]">call</span>
                <a href="tel:${hospital.phone}" class="text-blue-600 hover:text-blue-800">${hospital.phone}</a>
              </div>
            ` : ''}
            <div class="flex items-center gap-2 text-slate-600">
              <span class="material-symbols-outlined text-[14px]">route</span>
              <span>${hospital.distance_km ? hospital.distance_km.toFixed(1) : '?'} km</span>
            </div>
          </div>
        </div>
      `;

      marker.bindPopup(popupContent, {
        maxWidth: 300,
        className: 'custom-popup'
      });

      marker.on('click', () => {
        // Marker clicked
      });

      newMarkers.push(marker);
    });

    setMap(newMap);
    markersRef.current = newMarkers;

    // Fit bounds
    if (newMarkers.length > 0) {
      const group = new window.L.featureGroup(newMarkers);
      newMap.fitBounds(group.getBounds().pad(0.2));
    }
  };

  const handleBackToPhase1 = () => {
    setCurrentPhase(1);
    if (map) {
      map.remove();
      setMap(null);
    }
    markersRef.current = [];
    setRecommendations([]);
    setExpansionMessage(null);
  };

  const handleBackToSymptoms = () => {
    setCurrentPhase(0);
    setAssessmentResult(null);
    setPincode('');
  };

  // Phase 0: Symptom Selection
  if (currentPhase === 0) {
    return (
      <div className="flex flex-col min-h-screen bg-background-light dark:bg-background-dark">
        {/* Header */}
        <header className="sticky top-0 z-50 w-full border-b border-slate-200 dark:border-slate-800 bg-surface-light/80 dark:bg-surface-dark/80 backdrop-blur-md">
          <div className="px-4 md:px-10 h-16 flex items-center justify-between max-w-7xl mx-auto">
            <div className="flex items-center gap-3">
              <div className="size-8 rounded-lg bg-primary flex items-center justify-center text-white shadow-md">
                <span className="material-symbols-outlined text-[20px]">local_hospital</span>
              </div>
              <h2 className="text-lg font-bold">What's Up Doc</h2>
            </div>
            <div className="flex items-center gap-3">
              <div className="inline-flex items-center gap-2 px-3 py-1.5 rounded-full bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800">
                <span className="text-xs font-medium text-blue-700 dark:text-blue-400">Sample Data</span>
                <label className="relative inline-flex cursor-pointer items-center">
                  <input 
                    className="peer sr-only" 
                    type="checkbox" 
                    checked={useSampleData}
                    onChange={(e) => setUseSampleData(e.target.checked)}
                  />
                  <div className="h-5 w-9 rounded-full bg-slate-300 peer-checked:bg-blue-600 transition-colors"></div>
                  <div className="absolute left-0.5 top-0.5 h-4 w-4 rounded-full bg-white transition-all peer-checked:translate-x-full shadow-sm"></div>
                </label>
              </div>
            </div>
          </div>
        </header>

        {/* Main Content */}
        <main className="flex-grow flex items-center justify-center px-4 py-12">
          <SymptomSelector onAssessmentComplete={handleAssessmentComplete} />
        </main>
      </div>
    );
  }

  // Phase 1: Pincode Entry (after symptom assessment)
  if (currentPhase === 1) {
    const badge = getSeverityBadge(assessmentResult.severityLevel);
    const config = routingConfig[assessmentResult.severityLevel];

    return (
      <div className="flex flex-col min-h-screen bg-background-light dark:bg-background-dark">
        {/* Header */}
        <header className="sticky top-0 z-50 w-full border-b border-slate-200 dark:border-slate-800 bg-surface-light/80 dark:bg-surface-dark/80 backdrop-blur-md">
          <div className="px-4 md:px-10 h-16 flex items-center justify-between max-w-7xl mx-auto">
            <div className="flex items-center gap-3">
              <button 
                onClick={handleBackToSymptoms}
                className="flex items-center gap-2 text-slate-600 hover:text-slate-900 transition-colors p-2 rounded-lg hover:bg-slate-100"
              >
                <span className="material-symbols-outlined">arrow_back</span>
              </button>
              <div className="size-8 rounded-lg bg-primary flex items-center justify-center text-white shadow-md">
                <span className="material-symbols-outlined text-[20px]">local_hospital</span>
              </div>
              <h2 className="text-lg font-bold">What's Up Doc</h2>
            </div>
          </div>
        </header>

        {/* Main Content */}
        <main className="flex-grow flex flex-col items-center justify-center px-4 py-12">
          <div className="w-full max-w-2xl space-y-8">
            {/* Assessment Result Banner */}
            <div 
              className="rounded-2xl p-6 border-2 shadow-lg"
              style={{ 
                backgroundColor: badge.bgColor,
                borderColor: badge.borderColor
              }}
            >
              <div className="flex items-start gap-4">
                <div 
                  className="size-12 rounded-full flex items-center justify-center text-2xl"
                  style={{ backgroundColor: badge.color, color: 'white' }}
                >
                  {badge.icon}
                </div>
                <div className="flex-1">
                  <h3 className="text-xl font-bold text-slate-900 mb-2">
                    {badge.label} Priority Assessment
                  </h3>
                  <p className="text-slate-700 mb-3">{assessmentResult.recommendation}</p>
                  <div className="flex flex-wrap gap-2">
                    {assessmentResult.specialties.map(specialty => (
                      <span 
                        key={specialty}
                        className="px-3 py-1 rounded-full text-xs font-semibold bg-white/80 text-slate-700"
                      >
                        {specialty}
                      </span>
                    ))}
                  </div>
                </div>
              </div>
            </div>

            {/* Facility Type Info */}
            <div className="bg-white dark:bg-surface-dark rounded-2xl p-6 shadow-xl border border-slate-200">
              <h3 className="font-bold text-slate-900 mb-3">We'll search for:</h3>
              <div className="flex flex-wrap gap-2">
                {config.facilityTypes.map(type => (
                  <span 
                    key={type}
                    className="px-4 py-2 rounded-lg bg-slate-100 text-slate-700 font-medium"
                  >
                    {type}
                  </span>
                ))}
              </div>
              <p className="text-sm text-slate-600 mt-3">
                Initial search radius: {config.initialRadius}km
                {config.requiresEmergency24x7 && ' • 24/7 Emergency facilities only'}
              </p>
            </div>

            {/* Pincode Input */}
            <form onSubmit={handleSearch} className="space-y-6">
              <div className="relative group">
                <div className="absolute -inset-1 bg-gradient-to-r from-primary to-accent rounded-2xl blur opacity-25 group-hover:opacity-40 transition"></div>
                <div className="relative">
                  <input 
                    value={pincode}
                    onChange={(e) => setPincode(e.target.value.replace(/[^0-9]/g, '').slice(0, 6))}
                    className="w-full h-24 bg-white rounded-2xl border-2 border-slate-200 text-center text-5xl font-black tracking-[0.5em] text-slate-900 placeholder:text-slate-400 focus:border-primary focus:outline-none focus:ring-4 focus:ring-primary/10 shadow-xl transition-all" 
                    inputMode="numeric" 
                    maxLength="6" 
                    pattern="[0-9]*" 
                    placeholder="______" 
                    type="text"
                  />
                  <div className="absolute right-6 top-1/2 -translate-y-1/2 text-slate-400">
                    <span className="material-symbols-outlined text-3xl">location_on</span>
                  </div>
                </div>
                <p className="mt-4 text-sm text-slate-500 font-semibold text-center">Enter your 6-digit Pincode</p>
              </div>

              <button 
                type="submit"
                disabled={loading || pincode.length !== 6}
                className="w-full h-14 bg-primary hover:bg-primary-dark text-white font-bold text-lg rounded-xl shadow-lg transition-all disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-2"
              >
                {loading ? (
                  <>
                    <span className="animate-spin material-symbols-outlined">refresh</span>
                    Searching...
                  </>
                ) : (
                  <>
                    <span className="material-symbols-outlined">search</span>
                    Find Facilities
                  </>
                )}
              </button>
            </form>

            {/* Emergency Call Button for Emergency Cases */}
            {assessmentResult.severityLevel === 'emergency' && (
              <div className="bg-red-50 border-2 border-red-200 rounded-xl p-4">
                <div className="flex items-center gap-3">
                  <span className="material-symbols-outlined text-red-600 text-[32px]">emergency</span>
                  <div className="flex-1">
                    <h3 className="font-bold text-red-900 mb-1">Emergency Situation</h3>
                    <p className="text-sm text-red-700 mb-3">
                      If this is life-threatening, call emergency services now.
                    </p>
                    <a
                      href="tel:108"
                      className="inline-flex items-center gap-2 px-6 py-3 bg-red-600 hover:bg-red-700 text-white font-bold rounded-lg transition-colors"
                    >
                      <span className="material-symbols-outlined text-[24px]">call</span>
                      Call 108 Emergency
                    </a>
                  </div>
                </div>
              </div>
            )}
          </div>
        </main>
      </div>
    );
  }

  // Phase 2: Results (existing results view with severity enhancements)
  const badge = getSeverityBadge(assessmentResult.severityLevel);
  const config = routingConfig[assessmentResult.severityLevel];

  return (
    <div className="flex flex-col h-screen overflow-hidden">
      {/* Header with Severity Badge */}
      <header className="sticky top-0 z-50 w-full border-b border-slate-200 bg-white shadow-sm">
        <div className="px-4 h-16 flex items-center justify-between">
          <div className="flex items-center gap-3">
            <button 
              onClick={handleBackToPhase1}
              className="flex items-center gap-2 text-slate-600 hover:text-slate-900 p-2 rounded-lg hover:bg-slate-100"
            >
              <span className="material-symbols-outlined">arrow_back</span>
            </button>

            <div className="flex items-center gap-3">
              <div className="size-9 rounded-lg bg-primary flex items-center justify-center text-white shadow-md">
                <span className="material-symbols-outlined text-[20px]">local_hospital</span>
              </div>
              <div>
                <h2 className="text-lg font-bold">What's Up Doc</h2>
                <span className="text-xs text-slate-500">Severity-Based Results</span>
              </div>
            </div>
          </div>

          <div className="flex items-center gap-3">
            <div 
              className="px-4 py-2 rounded-full font-bold text-sm flex items-center gap-2"
              style={{ 
                backgroundColor: badge.bgColor,
                color: badge.color,
                border: `2px solid ${badge.borderColor}`
              }}
            >
              <span>{badge.icon}</span>
              <span>{badge.label} Priority</span>
            </div>
          </div>
        </div>
      </header>

      <main className="flex flex-1 overflow-hidden">
        {/* Sidebar with Results */}
        <aside className="w-full md:w-[450px] bg-white border-r flex flex-col">
          <div className="px-4 py-4 border-b space-y-4">
            <div className="flex items-center justify-between">
              <h3 className="font-bold text-slate-800">
                {recommendations.length} Facilities Found
              </h3>
              <div className="text-xs text-slate-500">
                Within {searchRadius}km
              </div>
            </div>

            {/* Expansion Message */}
            {expansionMessage && (
              <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-3 text-sm text-yellow-800">
                ⚠️ {expansionMessage}
              </div>
            )}

            {/* Emergency Banner */}
            {assessmentResult.severityLevel === 'emergency' && (
              <div className="bg-red-50 border-2 border-red-200 rounded-lg p-3">
                <div className="flex items-center gap-2 mb-2">
                  <span className="material-symbols-outlined text-red-600">emergency</span>
                  <span className="font-bold text-red-900">Emergency Mode</span>
                </div>
                <p className="text-xs text-red-700 mb-2">Showing nearest 24x7 facilities only</p>
                <a
                  href="tel:108"
                  className="inline-flex items-center gap-2 px-4 py-2 bg-red-600 hover:bg-red-700 text-white font-bold rounded-lg text-sm transition-colors w-full justify-center"
                >
                  <span className="material-symbols-outlined text-[18px]">call</span>
                  Call 108 Emergency
                </a>
              </div>
            )}
          </div>

          {/* Hospital List */}
          <div className="flex-1 overflow-y-auto p-3 space-y-3 bg-slate-50">
            {recommendations.length === 0 ? (
              <div className="text-center py-12">
                <span className="material-symbols-outlined text-slate-400 text-[64px]">search_off</span>
                <p className="text-slate-600 mt-4">No facilities found within 20km</p>
                <a
                  href="tel:108"
                  className="inline-flex items-center gap-2 px-6 py-3 bg-red-600 hover:bg-red-700 text-white font-bold rounded-lg mt-4"
                >
                  <span className="material-symbols-outlined">call</span>
                  Call 108 for Assistance
                </a>
              </div>
            ) : (
              recommendations.map((hospital, index) => (
                <div 
                  key={index}
                  className="bg-white rounded-xl p-4 border border-slate-200 shadow-sm hover:shadow-md transition-all cursor-pointer"
                >
                  <div className="flex items-start justify-between gap-2 mb-2">
                    <h4 className="font-bold text-slate-900">{hospital.name}</h4>
                    <span 
                      className="px-2 py-1 rounded text-xs font-bold whitespace-nowrap"
                      style={{ 
                        backgroundColor: badge.bgColor,
                        color: badge.color
                      }}
                    >
                      {hospital.facility_type}
                    </span>
                  </div>

                  <p className="text-sm text-slate-600 mb-3">{hospital.address}</p>

                  <div className="flex items-center justify-between">
                    <span className="text-xs text-slate-500">
                      📍 {hospital.distance_km ? hospital.distance_km.toFixed(1) : '?'} km away
                    </span>
                    {hospital.phone && (
                      <a
                        href={`tel:${hospital.phone}`}
                        className="px-3 py-1.5 bg-primary hover:bg-primary-dark text-white text-xs font-semibold rounded-lg transition-colors"
                      >
                        Call
                      </a>
                    )}
                  </div>
                </div>
              ))
            )}
          </div>
        </aside>

        {/* Map */}
        <section className="flex-1 relative">
          <div id="care-map" className="w-full h-full"></div>

          {/* Map Legend */}
          <div className="absolute top-4 left-4 z-[1000] bg-white rounded-lg shadow-lg p-3 border">
            <div className="text-sm font-semibold text-slate-900 mb-2">Search Radius</div>
            <div className="flex items-center gap-2">
              <div 
                className="w-4 h-4 rounded-full"
                style={{ backgroundColor: config.color }}
              ></div>
              <span className="text-sm text-slate-600">{searchRadius}km radius</span>
            </div>
            <div className="text-xs text-slate-500 mt-2">
              {config.level} priority facilities
            </div>
          </div>
        </section>
      </main>
    </div>
  );
}

export default AppWithSymptoms;
