import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [pincode, setPincode] = useState('');
  const [condition, setCondition] = useState('');
  const [isEmergency, setIsEmergency] = useState(false);
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    // Initialize the page
    const findHelpBtn = document.getElementById('find-help-btn');
    const emergencyToggle = document.getElementById('emergency-toggle');
    const pincodeInput = document.getElementById('pincode');
    const conditionInput = document.getElementById('condition');

    if (findHelpBtn) {
      findHelpBtn.addEventListener('click', handleSearch);
    }

    if (emergencyToggle) {
      emergencyToggle.addEventListener('change', handleEmergencyToggle);
    }

    if (pincodeInput) {
      pincodeInput.addEventListener('input', handlePincodeInput);
    }

    if (conditionInput) {
      conditionInput.addEventListener('input', (e) => setCondition(e.target.value));
    }

    return () => {
      if (findHelpBtn) findHelpBtn.removeEventListener('click', handleSearch);
      if (emergencyToggle) emergencyToggle.removeEventListener('change', handleEmergencyToggle);
      if (pincodeInput) pincodeInput.removeEventListener('input', handlePincodeInput);
      if (conditionInput) conditionInput.removeEventListener('input', (e) => setCondition(e.target.value));
    };
  }, []);

  const handlePincodeInput = (e) => {
    // Remove non-numeric characters and limit to 6 digits
    const value = e.target.value.replace(/[^0-9]/g, '').slice(0, 6);
    e.target.value = value;
    setPincode(value);
  };

  const handleEmergencyToggle = (e) => {
    const isChecked = e.target.checked;
    setIsEmergency(isChecked);
    toggleEmergencyState(e.target);
  };

  const toggleEmergencyState = (checkbox) => {
    const statusText = document.getElementById('toggle-status-text');
    const findBtn = document.getElementById('find-help-btn');
    const inputField = document.getElementById('pincode');

    if (checkbox.checked) {
      // Emergency State
      statusText.innerHTML = "<span class='font-bold text-emergency'>Priority Mode:</span> Bypassing filters for 24/7 Trauma Centers.";
      findBtn.classList.remove('bg-primary', 'hover:bg-primary-dark', 'shadow-glow', 'focus:ring-primary/30');
      findBtn.classList.add('bg-emergency', 'hover:bg-emergency-dark', 'shadow-emergency-glow', 'animate-pulse', 'focus:ring-emergency/30');

      // Input styling changes
      inputField.classList.add('border-emergency', 'text-emergency', 'focus:border-emergency', 'ring-emergency/20');
      inputField.classList.remove('focus:border-primary', 'focus:bg-white', 'focus:ring-primary/10');
      inputField.classList.add('bg-emergency/5', 'dark:bg-emergency/10');

      // Add pulse animation to input container
      inputField.parentElement.classList.add('animate-emergency');
    } else {
      // Normal State
      statusText.innerText = "Show routine care & clinics.";

      // Button Reset
      findBtn.classList.add('bg-primary', 'hover:bg-primary-dark', 'shadow-glow', 'focus:ring-primary/30');
      findBtn.classList.remove('bg-emergency', 'hover:bg-emergency-dark', 'shadow-emergency-glow', 'animate-pulse', 'focus:ring-emergency/30');

      // Input Reset
      inputField.classList.remove('border-emergency', 'text-emergency', 'focus:border-emergency', 'ring-emergency/20', 'bg-emergency/5', 'dark:bg-emergency/10');
      inputField.classList.add('focus:border-primary', 'focus:bg-white', 'focus:ring-primary/10');

      // Remove pulse animation
      inputField.parentElement.classList.remove('animate-emergency');
    }
  };

  const handleSearch = async () => {
    if (!pincode || pincode.length !== 6) {
      setError('Please enter a valid 6-digit pincode');
      return;
    }

    if (!condition.trim()) {
      setError('Please describe your medical condition');
      return;
    }

    setLoading(true);
    setError('');

    try {
      let endpoint = '/api/hospitals/recommend';
      const params = new URLSearchParams({
        condition: condition,
        pincode: pincode
      });

      if (isEmergency) {
        endpoint = '/api/hospitals/emergency';
        params.append('pincode', pincode);
      }

      const response = await axios.get(`${endpoint}?${params}`);

      if (isEmergency) {
        setRecommendations(response.data.nearestHospitals.map(hospital => ({
          ...hospital,
          category: hospital.category + ' (Emergency)',
          emergencyAvailable: true
        })));
      } else {
        setRecommendations(response.data.recommendations);
      }

      // Show results
      const resultsContainer = document.getElementById('results-container');
      if (resultsContainer) {
        resultsContainer.classList.remove('hidden');
        resultsContainer.scrollIntoView({ behavior: 'smooth' });
      }

    } catch (error) {
      console.error('Error searching hospitals:', error);
      setError('Unable to find hospitals for this pincode. Please try again.');
      setRecommendations([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (recommendations.length > 0) {
      renderHospitals();
    }
  }, [recommendations]);

  const renderHospitals = () => {
    const hospitalsGrid = document.getElementById('hospitals-grid');
    if (!hospitalsGrid) return;

    hospitalsGrid.innerHTML = recommendations.map((hospital, index) => `
      <div class="rounded-3xl bg-surface-light dark:bg-surface-dark p-6 shadow-xl ring-1 ring-gray-900/5 dark:ring-white/5 transition-all duration-300 hover:scale-[1.02]" style="animation-delay: ${index * 0.1}s">
        <div class="flex items-start gap-4 mb-4">
          <div class="flex size-12 items-center justify-center rounded-xl bg-primary/10 text-primary shadow-sm">
            <span class="material-symbols-outlined text-2xl">local_hospital</span>
          </div>
          <div class="flex-1">
            <h3 class="text-lg font-bold text-text-main-light dark:text-text-main-dark mb-1">${hospital.name}</h3>
            <p class="text-sm text-text-secondary-light dark:text-text-secondary-dark">${hospital.category}</p>
          </div>
        </div>

        <div class="space-y-3 mb-4">
          <div class="flex items-center gap-2 text-sm">
            <span class="material-symbols-outlined text-primary text-lg">location_on</span>
            <span class="text-text-secondary-light dark:text-text-secondary-dark">${hospital.address}</span>
          </div>

          ${hospital.phone ? `
            <div class="flex items-center gap-2 text-sm">
              <span class="material-symbols-outlined text-primary text-lg">call</span>
              <a href="tel:${hospital.phone}" class="text-primary font-medium hover:text-primary-dark">${hospital.phone}</a>
            </div>
          ` : ''}

          ${hospital.distance ? `
            <div class="flex items-center gap-2 text-sm">
              <span class="material-symbols-outlined text-primary text-lg">route</span>
              <span class="text-text-secondary-light dark:text-text-secondary-dark">${hospital.distance}</span>
            </div>
          ` : ''}
        </div>

        <div class="flex items-center justify-between pt-4 border-t border-slate-100 dark:border-slate-700">
          <div class="flex items-center gap-2">
            <span class="material-symbols-outlined text-lg ${hospital.cost === 'Free/Subsidized' ? 'text-primary' : 'text-orange-500'}">
              ${hospital.cost === 'Free/Subsidized' ? 'currency_rupee' : 'paid'}
            </span>
            <span class="text-sm font-medium text-text-main-light dark:text-text-main-dark">${hospital.cost}</span>
          </div>

          ${hospital.emergencyAvailable ? `
            <div class="flex items-center gap-1 rounded-full bg-emergency/10 px-3 py-1">
              <span class="material-symbols-outlined text-emergency text-sm">emergency</span>
              <span class="text-xs font-bold text-emergency">24x7</span>
            </div>
          ` : ''}
        </div>

        ${hospital.specialties && hospital.specialties.length > 0 ? `
          <div class="mt-4 pt-4 border-t border-slate-100 dark:border-slate-700">
            <p class="text-xs font-semibold text-text-secondary-light dark:text-text-secondary-dark mb-2">SPECIALTIES</p>
            <div class="flex flex-wrap gap-1">
              ${hospital.specialties.slice(0, 3).map(specialty => `
                <span class="inline-block rounded-full bg-primary/10 px-2 py-1 text-xs font-medium text-primary">
                  ${specialty.replace(/\\n/g, '').trim()}
                </span>
              `).join('')}
              ${hospital.specialties.length > 3 ? `
                <span class="inline-block rounded-full bg-slate-100 dark:bg-slate-700 px-2 py-1 text-xs font-medium text-text-secondary-light dark:text-text-secondary-dark">
                  +${hospital.specialties.length - 3} more
                </span>
              ` : ''}
            </div>
          </div>
        ` : ''}
      </div>
    `).join('');
  };

  return null; // This component doesn't render anything directly, it manipulates the existing HTML
}

export default App;
